        <Check className="h-4 w-4" />
      </MenubarPrimitive.ItemIndicator>
    </span>
    {children}
  </MenubarPrimitive.CheckboxItem>
))
MenubarCheckboxItem.displayName = MenubarPrimitive.CheckboxItem.displayName

const MenubarRadioItem = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.RadioItem>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.RadioItem>
>(({ className, children, ...props }, ref) => (
  <MenubarPrimitive.RadioItem
    ref={ref}
    className={cn(
      "relative flex cursor-default select-none items-center rounded-sm py-1.5 pl-8 pr-2 text-sm outline-none focus:bg-accent focus:text-accent-foreground data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
      className
    )}
    {...props}
  >
    <span className="absolute left-2 flex h-3.5 w-3.5 items-center justify-center">
      <MenubarPrimitive.ItemIndicator>
        <Circle className="h-2 w-2 fill-current" />
      </MenubarPrimitive.ItemIndicator>
    </span>
    {children}
  </MenubarPrimitive.RadioItem>
))
MenubarRadioItem.displayName = MenubarPrimitive.RadioItem.displayName

const MenubarLabel = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Label>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Label> & {
    inset?: boolean
  }
>(({ className, inset, ...props }, ref) => (
  <MenubarPrimitive.Label
    ref={ref}
    className={cn(
      "px-2 py-1.5 text-sm font-semibold",
      inset && "pl-8",
      className
    )}
    {...props}
  />
))
MenubarLabel.displayName = MenubarPrimitive.Label.displayName

const MenubarSeparator = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Separator>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Separator>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Separator
    ref={ref}
    className={cn("-mx-1 my-1 h-px bg-muted", className)}
    {...props}
  />
))
MenubarSeparator.displayName = MenubarPrimitive.Separator.displayName

const MenubarShortcut = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement>) => {
  return (
    <span
      className={cn(
        "ml-auto text-xs tracking-widest text-muted-foreground",
        className
      )}
      {...props}
    />
  )
}
MenubarShortcut.displayname = "MenubarShortcut"

export {
  Menubar,
  MenubarMenu,
  MenubarTrigger,
  MenubarContent,
  MenubarItem,
  MenubarSeparator,
  MenubarLabel,
  MenubarCheckboxItem,
  MenubarRadioGroup,
  MenubarRadioItem,
  MenubarPortal,
  MenubarSubContent,
  MenubarSubTrigger,
  MenubarGroup,
  MenubarSub,
  MenubarShortcut,
}

client/src/pages/Notifications.tsx.content:
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Bell, Check, Trash2 } from "lucide-react";
import { useApp } from "@/context/AppContext";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";

export default function Notifications() {
  const { 
    notifications, 
    markNotificationAsRead, 
    markAllNotificationsAsRead,
    deleteNotification,
    clearAllNotifications,
    unreadNotificationsCount 
  } = useApp();

  const handleMarkAsRead = (id: string) => {
    markNotificationAsRead(id);
  };

  const handleMarkAllAsRead = () => {
    markAllNotificationsAsRead();
  };

  const handleDelete = (id: string) => {
    deleteNotification(id);
  };

  const handleClearAll = () => {
    if (confirm('Are you sure you want to delete all notifications?')) {
      clearAllNotifications();
    }
  };

  const formatTime = (date: Date) => {
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days}d ago`;
    if (hours > 0) return `${hours}h ago`;
    if (minutes > 0) return `${minutes}m ago`;
    return 'Just now';
  };

  const unreadCount = unreadNotificationsCount;

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-4xl mx-auto px-6 py-8 space-y-6">
        <Breadcrumb data-testid="breadcrumb-notifications">
          <BreadcrumbList>
            <BreadcrumbItem>
              <BreadcrumbLink asChild>
                <Link href="/" data-testid="link-breadcrumb-home">Home</Link>
              </BreadcrumbLink>
            </BreadcrumbItem>
            <BreadcrumbSeparator />
            <BreadcrumbItem>
              <BreadcrumbPage data-testid="text-breadcrumb-notifications">Notifications</BreadcrumbPage>
            </BreadcrumbItem>
          </BreadcrumbList>
        </Breadcrumb>

        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-4xl font-bold tracking-tight flex items-center gap-3" data-testid="text-notifications-title">
              Notifications
              {unreadCount > 0 && (
                <Badge variant="destructive">{unreadCount}</Badge>
              )}
            </h1>
            <p className="text-muted-foreground mt-2">
              Stay updated with the latest AI services and changes
            </p>
          </div>

          <div className="flex items-center gap-2">
            {unreadCount > 0 && (
              <Button
                onClick={handleMarkAllAsRead}
                variant="outline"
                className="gap-2"
                data-testid="button-mark-all-read"
              >
                <Check className="h-4 w-4" />
                Mark all as read
              </Button>
            )}
            <Button
              onClick={handleClearAll}
              variant="outline"
              className="gap-2"
              disabled={notifications.length === 0}
              data-testid="button-clear-notifications"
            >
              <Trash2 className="h-4 w-4" />
              Clear all
            </Button>
          </div>
        </div>

        {notifications.length > 0 ? (
          <div className="space-y-3">
            {notifications.map(notification => (
              <Card
                key={notification.id}
                className={`transition-colors ${!notification.isRead ? 'bg-accent/20 border-accent' : ''}`}
                data-testid={`notification-${notification.id}`}
              >
                <CardContent className="p-4">
                  <div className="flex items-start gap-4">
                    <div className={`mt-1 ${notification.isRead ? 'text-muted-foreground' : 'text-primary'}`}>
                      <Bell className="h-5 w-5" />
                    </div>

                    <div className="flex-1 min-w-0">
                      <p className={`text-sm ${!notification.isRead ? 'font-medium' : 'text-muted-foreground'}`}>
                        {notification.message}
                      </p>
                      <div className="flex items-center gap-2 mt-2">
                        <Badge variant="secondary" className="text-xs capitalize">
                          {notification.category}
                        </Badge>
                        <span className="text-xs text-muted-foreground">
                          {formatTime(notification.createdAt)}
                        </span>
                      </div>
                    </div>

                    <div className="flex items-center gap-1 flex-shrink-0">
                      {!notification.isRead && (
                        <Button
                          size="icon"
                          variant="ghost"
                          onClick={() => handleMarkAsRead(notification.id)}
                          data-testid={`button-mark-read-${notification.id}`}
                        >
                          <Check className="h-4 w-4" />
                        </Button>
                      )}
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => handleDelete(notification.id)}
                        data-testid={`button-delete-${notification.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <div className="text-center py-24">
            <Bell className="h-16 w-16 text-muted-foreground mx-auto mb-4 opacity-20" />
            <p className="text-xl text-muted-foreground mb-2">No notifications</p>
            <p className="text-sm text-muted-foreground">
              You're all caught up!
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/context/AppContext.tsx.content:
import { createContext, useContext, useState, useEffect, ReactNode } from "react";

interface Notification {
  id: string;
  message: string;
  category: string;
  isRead: boolean;
  createdAt: Date;
}

interface AppContextType {
  favorites: Set<string>;
  comparing: Set<string>;
  history: string[];
  notifications: Notification[];
  selectedCategory: string;
  addFavorite: (id: string) => void;
  removeFavorite: (id: string) => void;
  toggleFavorite: (id: string) => void;
  clearFavorites: () => void;
  addToCompare: (id: string) => boolean;
  removeFromCompare: (id: string) => void;
  toggleCompare: (id: string) => boolean;
  clearCompare: () => void;
  addToHistory: (id: string) => void;
  clearHistory: () => void;
  markNotificationAsRead: (id: string) => void;
  markAllNotificationsAsRead: () => void;
  deleteNotification: (id: string) => void;
  clearAllNotifications: () => void;
  unreadNotificationsCount: number;
  setSelectedCategory: (category: string) => void;
}

const AppContext = createContext<AppContextType | undefined>(undefined);

export function AppProvider({ children }: { children: ReactNode }) {
  const [favorites, setFavorites] = useState<Set<string>>(() => {
    const saved = localStorage.getItem('favorites');
    return saved ? new Set(JSON.parse(saved)) : new Set();
  });

  const [comparing, setComparing] = useState<Set<string>>(() => {
    const saved = localStorage.getItem('comparing');
    return saved ? new Set(JSON.parse(saved)) : new Set();
  });

  const [history, setHistory] = useState<string[]>(() => {
    const saved = localStorage.getItem('history');
    return saved ? JSON.parse(saved) : [];
  });

  const [notifications, setNotifications] = useState<Notification[]>(() => {
    const saved = localStorage.getItem('notifications');
    if (saved) {
      return JSON.parse(saved).map((n: any) => ({
        ...n,
        createdAt: new Date(n.createdAt)
      }));
    }
    return [
      {
        id: "1",
        message: "New AI service 'Claude 3' added to the Copywriting category",
        category: "copywriting",
        isRead: false,
        createdAt: new Date(Date.now() - 1000 * 60 * 30),
      },
      {
        id: "2",
        message: "Price update: ChatGPT Plus is now $25/month",
        category: "chat",
        isRead: false,
        createdAt: new Date(Date.now() - 1000 * 60 * 60 * 2),
      },
    ];
  });

  const [selectedCategory, setSelectedCategory] = useState<string>("all");

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(Array.from(favorites)));
  }, [favorites]);

  useEffect(() => {
    localStorage.setItem('comparing', JSON.stringify(Array.from(comparing)));
  }, [comparing]);

  useEffect(() => {
    localStorage.setItem('history', JSON.stringify(history));
  }, [history]);

  useEffect(() => {
    localStorage.setItem('notifications', JSON.stringify(notifications));
  }, [notifications]);

  const addFavorite = (id: string) => {
    setFavorites(prev => new Set(Array.from(prev).concat(id)));
  };

  const removeFavorite = (id: string) => {
    setFavorites(prev => {
      const newSet = new Set(prev);
      newSet.delete(id);
      return newSet;
    });
  };

  const toggleFavorite = (id: string) => {
    if (favorites.has(id)) {
      removeFavorite(id);
    } else {
      addFavorite(id);
    }
  };

  const clearFavorites = () => {
    setFavorites(new Set());
  };

  const addToCompare = (id: string): boolean => {
    if (comparing.size >= 4) {
      return false;
    }
    setComparing(prev => new Set(Array.from(prev).concat(id)));
    return true;
  };

  const removeFromCompare = (id: string) => {
    setComparing(prev => {
      const newSet = new Set(prev);
      newSet.delete(id);
      return newSet;
    });
  };

  const toggleCompare = (id: string): boolean => {
    if (comparing.has(id)) {
      removeFromCompare(id);
      return true;
    } else {
      return addToCompare(id);
    }
  };

  const clearCompare = () => {
    setComparing(new Set());
  };

  const addToHistory = (id: string) => {
    setHistory(prev => {
      const filtered = prev.filter(item => item !== id);
      return [id, ...filtered].slice(0, 20);
    });
  };

  const clearHistory = () => {
    setHistory([]);
  };

  const markNotificationAsRead = (id: string) => {
    setNotifications(prev =>
      prev.map(notif =>
        notif.id === id ? { ...notif, isRead: true } : notif
      )
    );
  };

  const markAllNotificationsAsRead = () => {
    setNotifications(prev =>
      prev.map(notif => ({ ...notif, isRead: true }))
    );
  };

  const deleteNotification = (id: string) => {
    setNotifications(prev => prev.filter(notif => notif.id !== id));
  };

  const clearAllNotifications = () => {
    setNotifications([]);
  };

  const unreadNotificationsCount = notifications.filter(n => !n.isRead).length;

  const value = {
    favorites,
    comparing,
    history,
    notifications,
    selectedCategory,
    addFavorite,
    removeFavorite,
    toggleFavorite,
    clearFavorites,
    addToCompare,
    removeFromCompare,
    toggleCompare,
    clearCompare,
    addToHistory,
    clearHistory,
    markNotificationAsRead,
    markAllNotificationsAsRead,
    deleteNotification,
    clearAllNotifications,
    unreadNotificationsCount,
    setSelectedCategory,
  };

  return <AppContext.Provider value={value}>{children}</AppContext.Provider>;
}

export function useApp() {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within AppProvider');
  }
  return context;
}

client/src/pages/ServiceDetail.tsx.content:
import { useRoute, useLocation, Link } from "wouter";
import { MOCK_SERVICES } from "@/lib/mockData";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Star, Heart, GitCompare, ExternalLink, Check } from "lucide-react";
import { useEffect } from "react";
import RecommendedServices from "@/components/RecommendedServices";
import PurchaseDialog from "@/components/PurchaseDialog";
import { useApp } from "@/context/AppContext";
import { useToast } from "@/hooks/use-toast";
import { formatPrice } from "@/lib/utils";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";

export default function ServiceDetail() {
  const [, params] = useRoute("/service/:id");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { favorites, comparing, toggleFavorite, toggleCompare, addToHistory, setSelectedCategory } = useApp();

  const service = MOCK_SERVICES.find(s => s.id === params?.id);

  useEffect(() => {
    if (service) {
      addToHistory(service.id);
    }
  }, [service?.id]);

  const handleRecommendedServiceClick = (serviceId: string) => {
    setLocation(`/service/${serviceId}`);
  };

  if (!service) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-2">Service not found</h1>
          <p className="text-muted-foreground">The service you're looking for doesn't exist.</p>
        </div>
      </div>
    );
  }

  const handleFavoriteToggle = () => {
    if (service) {
      toggleFavorite(service.id);
    }
  };

  const handleCompareToggle = () => {
    if (service) {
      const success = toggleCompare(service.id);
      if (!success && !comparing.has(service.id)) {
        toast({
          title: "Comparison limit reached",
          description: "You can compare up to 4 services at once",
          variant: "destructive",
        });
      }
    }
  };

  const isFavorite = service ? favorites.has(service.id) : false;
  const isComparing = service ? comparing.has(service.id) : false;

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-6xl mx-auto px-6 py-8 space-y-6">
        <Breadcrumb data-testid="breadcrumb-service-detail">
          <BreadcrumbList>
            <BreadcrumbItem>
              <BreadcrumbLink asChild>
                <Link href="/" data-testid="link-breadcrumb-home">Home</Link>
              </BreadcrumbLink>
            </BreadcrumbItem>
            <BreadcrumbSeparator />
            <BreadcrumbItem>
              <BreadcrumbLink asChild>
                <Link 
                  href="/"
                  onClick={(e) => {
                    e.preventDefault();
                    setSelectedCategory(service.category);
                    setLocation('/');
                  }}
                  data-testid="link-breadcrumb-category"
                >
                  {service.category.charAt(0).toUpperCase() + service.category.slice(1)}
                </Link>
              </BreadcrumbLink>
            </BreadcrumbItem>
            <BreadcrumbSeparator />
            <BreadcrumbItem>
              <BreadcrumbPage data-testid="text-breadcrumb-service">{service.name}</BreadcrumbPage>
            </BreadcrumbItem>
          </BreadcrumbList>
        </Breadcrumb>

        <Card className="border-card-border">
          <CardHeader className="space-y-6 pb-6">
            <div className="flex items-start gap-6">
              <div 
                className="flex items-center justify-center h-24 w-24 rounded-md overflow-hidden flex-shrink-0"
                style={{ backgroundColor: service.logoUrl ? '#ffffff' : service.color }}
              >
                {service.logoUrl ? (
                  <img 
                    src={service.logoUrl} 
                    alt={`${service.name} logo`} 
                    className="h-16 w-16 object-contain"
                    onError={(e) => {
                      const target = e.target as HTMLImageElement;
                      target.style.display = 'none';
                      const parent = target.parentElement;
                      if (parent) {
                        parent.style.backgroundColor = service.color;
                        parent.innerHTML = `<span class="text-4xl font-bold text-white">${service.name.charAt(0)}</span>`;
                      }
                    }}
                  />
                ) : (
                  <span className="text-4xl font-bold text-white">{service.name.charAt(0)}</span>
                )}
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-start justify-between gap-4 mb-3">
                  <div>
                    <h1 className="text-3xl font-bold tracking-tight mb-2" data-testid="text-service-detail-name">
                      {service.name}
                    </h1>
                    <p className="text-lg text-muted-foreground">{service.subtitle}</p>
                  </div>

                  <div className="flex items-center gap-2 flex-shrink-0">
                    <PurchaseDialog service={service} />
                    <Button
                      variant={isFavorite ? "default" : "outline"}
                      onClick={handleFavoriteToggle}
                      className="gap-2"
                      data-testid="button-favorite-detail"
                    >
                      <Heart className={`h-4 w-4 ${isFavorite ? 'fill-current' : ''}`} />
                      {isFavorite ? 'Saved' : 'Save'}
                    </Button>
                    <Button
                      variant={isComparing ? "default" : "outline"}
                      onClick={handleCompareToggle}
                      className="gap-2"
                      data-testid="button-compare-detail"
                    >
                      <GitCompare className="h-4 w-4" />
                      {isComparing ? 'Comparing' : 'Compare'}
                    </Button>
                  </div>
                </div>

                <div className="flex items-center gap-4 flex-wrap">
                  <div className="flex items-center gap-1">
                    <Star className="h-5 w-5 fill-yellow-500 text-yellow-500" />
                    <span className="font-semibold text-lg">{service.rating}</span>
                  </div>
                  <Badge variant="secondary" className="text-sm">{formatPrice(service.price)}</Badge>
                  <Badge 
                    variant="outline" 
                    className="text-sm capitalize cursor-pointer hover-elevate" 
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedCategory(service.category);
                      setLocation('/');
                    }}
                    data-testid="badge-category"
                  >
                    {service.category}
                  </Badge>
                  {service.website && (
                    <a href={service.website} target="_blank" rel="noopener noreferrer">
                      <Button variant="ghost" className="gap-2 px-2">
                        Visit Website
                        <ExternalLink className="h-4 w-4" />
                      </Button>
                    </a>
                  )}
                </div>
              </div>
            </div>
          </CardHeader>

          <CardContent className="space-y-8 pt-6 border-t">
            <div>
              <h2 className="text-xl font-semibold mb-3">About</h2>
              <p className="text-muted-foreground leading-relaxed">{service.fullDescription}</p>
            </div>

            <div>
              <h2 className="text-xl font-semibold mb-4">Key Features</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                {service.features.map((feature, index) => (
                  <div key={index} className="flex items-start gap-2">
                    <Check className="h-5 w-5 text-primary mt-0.5 flex-shrink-0" />
                    <span className="text-muted-foreground">{feature}</span>
                  </div>
                ))}
              </div>
            </div>

            <div>
              <h2 className="text-xl font-semibold mb-4">Pricing Plans</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {service.pricingTiers.map((tier: any, index: number) => (
                  <Card key={index} className={index === 1 ? 'border-primary' : ''}>
                    <CardHeader className="pb-4">
                      <h3 className="font-semibold text-lg">{tier.name}</h3>
                      <p className="text-2xl font-bold">{tier.price}</p>
                    </CardHeader>
                    <CardContent className="space-y-2">
                      {tier.features.map((feature: string, idx: number) => (
                        <div key={idx} className="flex items-start gap-2">
                          <Check className="h-4 w-4 text-primary mt-0.5 flex-shrink-0" />
                          <span className="text-sm text-muted-foreground">{feature}</span>
                        </div>
                      ))}
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>

            <div>
              <h2 className="text-xl font-semibold mb-4">Use Cases</h2>
              <div className="flex flex-wrap gap-2">
                {service.useCases.map((useCase, index) => (
                  <Badge key={index} variant="outline" className="text-sm">
                    {useCase}
                  </Badge>
                ))}
              </div>
            </div>
          </CardContent>
        </Card>

        <RecommendedServices
          currentServiceId={service.id}
          category={service.category}
          onServiceClick={handleRecommendedServiceClick}
        />
