import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Wallet, Building2, User } from "lucide-react";
import { type Payer, PAYMENT_METHODS } from "@/lib/payersData";
import { useToast } from "@/hooks/use-toast";
import { Card } from "@/components/ui/card";

interface TopUpDialogProps {
  payers: Payer[];
  onTopUp: (payerId: string, amount: number, method: string) => void;
}

export default function TopUpDialog({ payers, onTopUp }: TopUpDialogProps) {
  const [open, setOpen] = useState(false);
  const [selectedPayer, setSelectedPayer] = useState("");
  const [amount, setAmount] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const { toast } = useToast();

  const quickAmounts = [500, 1000, 2000, 5000, 10000];

  const handleTopUp = () => {
    const amountNum = parseFloat(amount);

    if (!selectedPayer) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞",
        variant: "destructive",
      });
      return;
    }

    if (!amountNum || amountNum <= 0) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—É—é —Å—É–º–º—É",
        variant: "destructive",
      });
      return;
    }

    const method = PAYMENT_METHODS.find(m => m.id === paymentMethod);
    onTopUp(selectedPayer, amountNum, method?.name || paymentMethod);

    toast({
      title: "–ë–∞–ª–∞–Ω—Å –ø–æ–ø–æ–ª–Ω–µ–Ω",
      description: `–£—Å–ø–µ—à–Ω–æ –ø–æ–ø–æ–ª–Ω–µ–Ω–æ –Ω–∞ ${amountNum} ‚ÇΩ`,
    });

    setOpen(false);
    setAmount("");
    setSelectedPayer("");
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button variant="default" className="gap-2 bg-green-600 hover:bg-green-700" data-testid="button-top-up">
          <Wallet className="h-4 w-4" />
          –ü–æ–ø–æ–ª–Ω–∏—Ç—å –±–∞–ª–∞–Ω—Å
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-lg">
        <DialogHeader>
          <DialogTitle>–ü–æ–ø–æ–ª–Ω–∏—Ç—å –±–∞–ª–∞–Ω—Å</DialogTitle>
          <DialogDescription>
            –í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞ –∏ —Å—É–º–º—É –¥–ª—è –ø–æ–ø–æ–ª–Ω–µ–Ω–∏—è
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          <div className="space-y-3">
            <Label>–ü–ª–∞—Ç–µ–ª—å—â–∏–∫–∏</Label>
            <div className="space-y-2">
              {payers.map((payer) => (
                <Card
                  key={payer.id}
                  className={`p-4 cursor-pointer hover-elevate transition-all ${
                    selectedPayer === payer.id ? "ring-2 ring-primary" : ""
                  }`}
                  onClick={() => setSelectedPayer(payer.id)}
                  data-testid={`select-payer-${payer.id}`}
                >
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-md bg-primary/10 flex items-center justify-center">
                      {payer.type === "company" ? (
                        <Building2 className="h-5 w-5 text-primary" />
                      ) : (
                        <User className="h-5 w-5 text-primary" />
                      )}
                    </div>
                    <div className="flex-1">
                      <p className="font-medium">{payer.name}</p>
                      <p className="text-sm text-muted-foreground">
                        –¢–∏–ø: {payer.type === "company" ? "company" : "individual"}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-muted-foreground">–ë–∞–ª–∞–Ω—Å:</p>
                      <p className="font-semibold">
                        {payer.balance.toLocaleString('ru-RU')} ‚ÇΩ
                      </p>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="amount">–ü–æ–ø–æ–ª–Ω–∏—Ç—å —Å—á–µ—Ç –Ω–∞ *</Label>
            <Input
              id="amount"
              type="number"
              placeholder="2000"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              data-testid="input-top-up-amount"
            />
            <div className="flex gap-2 flex-wrap mt-2">
              {quickAmounts.map((qa) => (
                <Button
                  key={qa}
                  variant="outline"
                  size="sm"
                  onClick={() => setAmount(qa.toString())}
                  data-testid={`button-quick-amount-${qa}`}
                >
                  {qa} ‚ÇΩ
                </Button>
              ))}
            </div>
          </div>

          <div className="space-y-2">
            <Label>–í—ã–±–µ—Ä–∏—Ç–µ —Å–ø–æ—Å–æ–± –æ–ø–ª–∞—Ç—ã</Label>
            <RadioGroup value={paymentMethod} onValueChange={setPaymentMethod}>
              {PAYMENT_METHODS.map((method) => (
                <div 
                  key={method.id}
                  className="flex items-center space-x-2 p-3 border border-border rounded-md hover-elevate cursor-pointer"
                  onClick={() => setPaymentMethod(method.id)}
                >
                  <RadioGroupItem value={method.id} id={method.id} />
                  <Label htmlFor={method.id} className="flex items-center gap-3 cursor-pointer flex-1">
                    <span className="text-2xl">{method.icon}</span>
                    <div>
                      <p className="font-medium">{method.name}</p>
                      <p className="text-sm text-muted-foreground">{method.description}</p>
                    </div>
                  </Label>
                </div>
              ))}
            </RadioGroup>
          </div>
        </div>

        <div className="flex justify-end gap-2">
          <Button variant="outline" onClick={() => setOpen(false)}>
            –û—Ç–º–µ–Ω–∞
          </Button>
          <Button onClick={handleTopUp} data-testid="button-confirm-top-up">
            –ü–æ–ø–æ–ª–Ω–∏—Ç—å
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/JobImpact.tsx.content:
import { useState, useMemo } from "react";
import { useLocation } from "wouter";
import { Search, ArrowUpDown, ArrowUp, ArrowDown } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";
import { MOCK_JOBS } from "@/lib/mockData";

type SortField = "title" | "aiImpact" | "tasksCount" | "aisCount";
type SortOrder = "asc" | "desc";

export default function JobImpact() {
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [sortField, setSortField] = useState<SortField>("aiImpact");
  const [sortOrder, setSortOrder] = useState<SortOrder>("desc");

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("desc");
    }
  };

  const handleJobClick = (jobTitle: string) => {
    setLocation(`/?jobTitle=${encodeURIComponent(jobTitle)}`);
  };

  const filteredAndSortedJobs = useMemo(() => {
    let filtered = MOCK_JOBS;

    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (job) =>
          job.title.toLowerCase().includes(query) ||
          job.category.toLowerCase().includes(query) ||
          job.description.toLowerCase().includes(query)
      );
    }

    return [...filtered].sort((a, b) => {
      let aValue = a[sortField];
      let bValue = b[sortField];

      if (typeof aValue === "string") {
        aValue = aValue.toLowerCase();
        bValue = (bValue as string).toLowerCase();
      }

      if (sortOrder === "asc") {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });
  }, [searchQuery, sortField, sortOrder]);

  const getImpactColor = (impact: number) => {
    if (impact >= 80) return "bg-red-500/10 text-red-500 border-red-500/20";
    if (impact >= 60) return "bg-orange-500/10 text-orange-500 border-orange-500/20";
    if (impact >= 40) return "bg-yellow-500/10 text-yellow-500 border-yellow-500/20";
    return "bg-green-500/10 text-green-500 border-green-500/20";
  };

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field) {
      return <ArrowUpDown className="h-4 w-4" />;
    }
    return sortOrder === "asc" ? (
      <ArrowUp className="h-4 w-4" />
    ) : (
      <ArrowDown className="h-4 w-4" />
    );
  };

  return (
    <div className="min-h-screen p-6 space-y-6">
      <Breadcrumb>
        <BreadcrumbList>
          <BreadcrumbItem>
            <BreadcrumbLink href="/">Home</BreadcrumbLink>
          </BreadcrumbItem>
          <BreadcrumbSeparator />
          <BreadcrumbItem>
            <BreadcrumbPage>Job Impact Index</BreadcrumbPage>
          </BreadcrumbItem>
        </BreadcrumbList>
      </Breadcrumb>

      <div className="space-y-4">
        <div>
          <h1 className="text-3xl font-bold">Job Impact Index</h1>
          <p className="text-muted-foreground mt-2">
            Analyze how AI is transforming different professions
          </p>
        </div>

        <div className="bg-card border border-card-border rounded-md p-6 space-y-3">
          <h2 className="text-lg font-semibold">Impact of AI on Jobs</h2>
          <p className="text-sm text-muted-foreground leading-relaxed">
            The Impact score as computed by TAAFT is expressed as a percentage where <strong>0%</strong> indicates that AI has no impact on that job and <strong>100%</strong> indicates that AI impacts almost every aspect of that job as it was before AI (this does not necessarily mean that the job will cease to exist, only that it's likely to change).
          </p>
          <div className="pt-2">
            <h3 className="text-sm font-semibold mb-2">How the Impact score is calculated</h3>
            <p className="text-sm text-muted-foreground leading-relaxed">
              The Impact score takes into account which tasks can be performed with AI, as well as factors such as the relevancy of those tasks for each job, the AI impact score of each task, the number of AIs available for each task and the capabilities of each individual AI within each task.
            </p>
          </div>
        </div>

        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search for a specific job..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-job-search"
          />
        </div>

        <div className="bg-card border border-card-border rounded-md overflow-hidden">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="w-[50px]">#</TableHead>
                <TableHead>
                  <button
                    onClick={() => handleSort("title")}
                    className="flex items-center gap-2 hover-elevate active-elevate-2 -mx-2 px-2 py-1 rounded-md transition-colors"
                    data-testid="sort-title"
                  >
                    Job Title
                    <SortIcon field="title" />
                  </button>
                </TableHead>
                <TableHead>
                  <button
                    onClick={() => handleSort("aiImpact")}
                    className="flex items-center gap-2 hover-elevate active-elevate-2 -mx-2 px-2 py-1 rounded-md transition-colors"
                    data-testid="sort-impact"
                  >
                    AI Impact
                    <SortIcon field="aiImpact" />
                  </button>
                </TableHead>
                <TableHead>
                  <button
                    onClick={() => handleSort("tasksCount")}
                    className="flex items-center gap-2 hover-elevate active-elevate-2 -mx-2 px-2 py-1 rounded-md transition-colors"
                    data-testid="sort-tasks"
                  >
                    Tasks
                    <SortIcon field="tasksCount" />
                  </button>
                </TableHead>
                <TableHead>
                  <button
                    onClick={() => handleSort("aisCount")}
                    className="flex items-center gap-2 hover-elevate active-elevate-2 -mx-2 px-2 py-1 rounded-md transition-colors"
                    data-testid="sort-ais"
                  >
                    AIs
                    <SortIcon field="aisCount" />
                  </button>
                </TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredAndSortedJobs.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={5} className="text-center text-muted-foreground py-8">
                    No jobs found matching your search
                  </TableCell>
                </TableRow>
              ) : (
                filteredAndSortedJobs.map((job, index) => (
                  <TableRow 
                    key={job.id} 
                    data-testid={`row-job-${job.id}`}
                    onClick={() => handleJobClick(job.title)}
                    className="cursor-pointer hover-elevate"
                  >
                    <TableCell className="font-medium text-muted-foreground">
                      {index + 1}
                    </TableCell>
                    <TableCell>
                      <div>
                        <div className="font-medium" data-testid={`text-job-title-${job.id}`}>
                          {job.title}
                        </div>
                        <div className="text-sm text-muted-foreground mt-1">
                          {job.description}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge
                        variant="outline"
                        className={getImpactColor(job.aiImpact)}
                        data-testid={`badge-impact-${job.id}`}
                      >
                        {job.aiImpact}%
                      </Badge>
                    </TableCell>
                    <TableCell data-testid={`text-tasks-${job.id}`}>
                      {job.tasksCount}
                    </TableCell>
                    <TableCell data-testid={`text-ais-${job.id}`}>
                      {job.aisCount}
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </div>

        <div className="text-sm text-muted-foreground">
          Showing {filteredAndSortedJobs.length} of {MOCK_JOBS.length} jobs
        </div>
      </div>
    </div>
  );
}

