import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { 
  ShoppingCart, 
  Trash2, 
  Building2, 
  User, 
  AlertTriangle,
  CheckCircle2,
  ShoppingBag,
  ChevronDown,
  ChevronUp,
  Clock
} from "lucide-react";
import { CartItem, getCartFromStorage, removeFromCart, clearCart, updateCartItemCredentials } from "@/lib/cartData";
import { Payer, Transaction } from "@/lib/payersData";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";

export default function Cart() {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [payers, setPayers] = useState<Payer[]>([]);
  const [selectedPayerId, setSelectedPayerId] = useState<string>("");
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());
  const [showSuccess, setShowSuccess] = useState(false);
  const [purchasedItemsCount, setPurchasedItemsCount] = useState(0);
  const [purchasedTotal, setPurchasedTotal] = useState(0);
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  useEffect(() => {
    setCartItems(getCartFromStorage());

    const stored = localStorage.getItem("payers");
    if (stored) {
      const payersData = JSON.parse(stored);
      setPayers(payersData);
      if (payersData.length > 0) {
        setSelectedPayerId(payersData[0].id);
      }
    }
  }, []);

  const handleRemoveItem = (itemId: string) => {
    removeFromCart(itemId);
    setCartItems(getCartFromStorage());
    toast({
      title: "–£–¥–∞–ª–µ–Ω–æ –∏–∑ –∫–æ—Ä–∑–∏–Ω—ã",
      description: "–°–µ—Ä–≤–∏—Å —É—Å–ø–µ—à–Ω–æ —É–¥–∞–ª–µ–Ω –∏–∑ –∫–æ—Ä–∑–∏–Ω—ã",
    });
  };

  const toggleExpanded = (itemId: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);
  };

  const handleCredentialsUpdate = (
    itemId: string,
    field: "login" | "password" | "paymentUrl",
    value: string
  ) => {
    const item = cartItems.find(i => i.id === itemId);
    if (!item) return;

    const credentials = {
      login: field === "login" ? value : item.login,
      password: field === "password" ? value : item.password,
      paymentUrl: field === "paymentUrl" ? value : item.paymentUrl,
    };

    updateCartItemCredentials(itemId, credentials);
    setCartItems(getCartFromStorage());
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);
  const selectedPayer = payers.find(p => p.id === selectedPayerId);
  const hasInsufficientFunds = selectedPayer ? selectedPayer.balance < totalPrice : true;

  const handleCheckout = () => {
    if (!selectedPayer) {
      toast({
        title: "–í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞",
        description: "–ù–µ–æ–±—Ö–æ–¥–∏–º–æ –≤—ã–±—Ä–∞—Ç—å –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞ –¥–ª—è –æ–ø–ª–∞—Ç—ã",
        variant: "destructive",
      });
      return;
    }

    if (hasInsufficientFunds) {
      toast({
        title: "–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤",
        description: "–£ –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞ –Ω–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤",
        variant: "destructive",
      });
      return;
    }

    // Save purchase info for success screen
    setPurchasedItemsCount(cartItems.length);
    setPurchasedTotal(totalPrice);

    // Process all purchases
    cartItems.forEach(item => {
      const purchase = {
        id: Date.now().toString() + Math.random(),
        serviceId: item.serviceId,
        planName: item.planName,
        price: item.price,
        purchaseDate: new Date().toISOString(),
        status: "active" as const,
        billingCycle: item.billingCycle,
        payerId: selectedPayerId,
        login: item.login,
        password: item.password,
        paymentUrl: item.paymentUrl,
      };

      const existing = localStorage.getItem("userPurchases");
      const purchases = existing ? JSON.parse(existing) : [];
      purchases.push(purchase);
      localStorage.setItem("userPurchases", JSON.stringify(purchases));

      // Add transaction
      const newTransaction: Transaction = {
        id: Date.now().toString() + Math.random(),
        payerId: selectedPayerId,
        date: new Date().toISOString(),
        type: "purchase",
        amount: item.price,
        comment: `–ü–æ–∫—É–ø–∫–∞ ${item.serviceName} - ${item.planName}`,
        serviceId: item.serviceId,
        serviceName: item.serviceName,
      };
      const existingTransactions = localStorage.getItem("transactions");
      const transactions = existingTransactions ? JSON.parse(existingTransactions) : [];
      transactions.push(newTransaction);
      localStorage.setItem("transactions", JSON.stringify(transactions));
    });

    // Deduct total from payer balance
    const updatedPayers = payers.map(p => 
      p.id === selectedPayerId ? { ...p, balance: p.balance - totalPrice } : p
    );
    setPayers(updatedPayers);
    localStorage.setItem("payers", JSON.stringify(updatedPayers));

    // Clear cart
    clearCart();
    setCartItems([]);

    // Show success screen
    setShowSuccess(true);
  };

  if (showSuccess) {
    return (
      <div className="max-w-4xl mx-auto px-6 py-8 space-y-6">
        <Breadcrumb>
          <BreadcrumbList>
            <BreadcrumbItem>
              <BreadcrumbLink href="/">Home</BreadcrumbLink>
            </BreadcrumbItem>
            <BreadcrumbSeparator />
            <BreadcrumbItem>
              <BreadcrumbPage>–ö–æ—Ä–∑–∏–Ω–∞</BreadcrumbPage>
            </BreadcrumbItem>
          </BreadcrumbList>
        </Breadcrumb>

        <div className="flex flex-col items-center text-center space-y-6 py-8">
          <div className="w-24 h-24 bg-green-500/10 rounded-full flex items-center justify-center">
            <CheckCircle2 className="h-16 w-16 text-green-500" />
          </div>

          <div className="space-y-2">
            <h1 className="text-3xl font-bold">–°–ø–∞—Å–∏–±–æ –∑–∞ –≤–∞—à –∑–∞–∫–∞–∑!</h1>
            <p className="text-muted-foreground max-w-md">
              –í–∞—à –∑–∞–∫–∞–∑ –ø—Ä–∏–Ω—è—Ç –∏ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –≤ –æ–±—Ä–∞–±–æ—Ç–∫–µ. –í—ã –º–æ–∂–µ—Ç–µ –ø—Ä–æ–≤–µ—Ä–∏—Ç—å —Å—Ç–∞—Ç—É—Å –∑–∞–∫–∞–∑–∞ –≤ –ª–∏—á–Ω–æ–º –∫–∞–±–∏–Ω–µ—Ç–µ.
            </p>
          </div>

          <Card className="bg-muted/50 w-full max-w-md">
            <CardContent className="pt-6">
              <div className="space-y-3 text-sm">
                <div className="flex justify-between">
                  <span className="text-muted-foreground">–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å–µ—Ä–≤–∏—Å–æ–≤:</span>
                  <span className="font-medium">{purchasedItemsCount}</span>
                </div>
                <div className="flex justify-between pt-3 border-t">
                  <span className="text-muted-foreground">–û–±—â–∞—è —Å—É–º–º–∞:</span>
                  <span className="font-bold text-lg">${purchasedTotal.toFixed(2)}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-blue-500/10 border-blue-500/20 w-full max-w-md">
            <CardContent className="pt-6">
              <div className="flex items-start gap-3">
                <Clock className="h-5 w-5 text-blue-500 mt-0.5 flex-shrink-0" />
                <div className="space-y-1 text-left">
                  <h4 className="font-semibold text-blue-500">–°—Ä–æ–∫ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∑–∞–∫–∞–∑–∞</h4>
                  <p className="text-sm text-muted-foreground">
                    –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤–∞—à–µ–≥–æ –∑–∞–∫–∞–∑–∞ –∑–∞–Ω–∏–º–∞–µ—Ç –æ—Ç 1 –¥–æ 7 –¥–Ω–µ–π. –ü–æ—Å–ª–µ –∞–∫—Ç–∏–≤–∞—Ü–∏–∏ —Å–µ—Ä–≤–∏—Å–æ–≤ –≤—ã –ø–æ–ª—É—á–∏—Ç–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ, –∏ –æ–Ω–∏ –æ—Ç–æ–±—Ä–∞–∑—è—Ç—Å—è –≤ –≤–∞—à–µ–º –ª–∏—á–Ω–æ–º –∫–∞–±–∏–Ω–µ—Ç–µ.
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>

          <div className="flex gap-3 pt-4 w-full max-w-md">
            <Button 
              variant="outline" 
              className="flex-1" 
              onClick={() => setLocation("/")}
              data-testid="button-continue-browsing"
            >
              –ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø—Ä–æ—Å–º–æ—Ç—Ä
            </Button>
            <Button 
              className="flex-1" 
              onClick={() => setLocation("/account")}
              data-testid="button-view-account"
            >
              –õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç
            </Button>
          </div>
        </div>
      </div>
    );
  }

  if (cartItems.length === 0) {
    return (
      <div className="max-w-7xl mx-auto px-6 py-8 space-y-6">
        <Breadcrumb>
          <BreadcrumbList>
            <BreadcrumbItem>
              <BreadcrumbLink href="/">Home</BreadcrumbLink>
            </BreadcrumbItem>
            <BreadcrumbSeparator />
            <BreadcrumbItem>
              <BreadcrumbPage>–ö–æ—Ä–∑–∏–Ω–∞</BreadcrumbPage>
            </BreadcrumbItem>
          </BreadcrumbList>
        </Breadcrumb>

        <div className="text-center py-16">
          <ShoppingCart className="h-24 w-24 mx-auto text-muted-foreground mb-4" />
          <h2 className="text-2xl font-bold mb-2">–ö–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞</h2>
          <p className="text-muted-foreground mb-6">
            –î–æ–±–∞–≤—å—Ç–µ —Å–µ—Ä–≤–∏—Å—ã –≤ –∫–æ—Ä–∑–∏–Ω—É —á—Ç–æ–±—ã –æ—Ñ–æ—Ä–º–∏—Ç—å –ø–æ–∫—É–ø–∫—É
          </p>
          <Button onClick={() => setLocation("/")} data-testid="button-browse-services">
            –ü–µ—Ä–µ–π—Ç–∏ –∫ –∫–∞—Ç–∞–ª–æ–≥—É
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-6 py-8 space-y-6">
      <Breadcrumb>
        <BreadcrumbList>
          <BreadcrumbItem>
            <BreadcrumbLink href="/">Home</BreadcrumbLink>
          </BreadcrumbItem>
          <BreadcrumbSeparator />
          <BreadcrumbItem>
            <BreadcrumbPage>–ö–æ—Ä–∑–∏–Ω–∞</BreadcrumbPage>
          </BreadcrumbItem>
        </BreadcrumbList>
      </Breadcrumb>

      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-3">
            <ShoppingCart className="h-8 w-8" />
            –ö–æ—Ä–∑–∏–Ω–∞
          </h1>
          <p className="text-muted-foreground mt-1">
            {cartItems.length} {cartItems.length === 1 ? "—Å–µ—Ä–≤–∏—Å" : "—Å–µ—Ä–≤–∏—Å–æ–≤"} –≤ –∫–æ—Ä–∑–∏–Ω–µ
          </p>
        </div>
        <Button 
          variant="outline" 
          onClick={() => {
            clearCart();
            setCartItems([]);
            toast({ title: "–ö–æ—Ä–∑–∏–Ω–∞ –æ—á–∏—â–µ–Ω–∞" });
          }}
          data-testid="button-clear-cart"
        >
          –û—á–∏—Å—Ç–∏—Ç—å –∫–æ—Ä–∑–∏–Ω—É
        </Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 space-y-4">
          {cartItems.map((item) => {
            const isExpanded = expandedItems.has(item.id);
            return (
              <Card key={item.id} className="hover-elevate" data-testid={`cart-item-${item.id}`}>
                <CardContent className="p-6">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex items-start gap-4 flex-1">
                      {item.serviceLogoUrl ? (
                        <img 
                          src={item.serviceLogoUrl} 
                          alt={item.serviceName}
                          className="w-16 h-16 object-contain rounded-md"
                        />
                      ) : (
                        <div 
                          className="w-16 h-16 rounded-md flex items-center justify-center"
                          style={{ backgroundColor: item.serviceColor }}
                        >
                          <span className="text-white font-bold text-xl">
                            {item.serviceName.charAt(0)}
                          </span>
                        </div>
                      )}
                      <div className="flex-1">
                        <h3 className="font-semibold text-lg" data-testid={`text-service-name-${item.id}`}>
                          {item.serviceName}
                        </h3>
                        <p className="text-sm text-muted-foreground">
                          {item.planName}
                        </p>
                        <div className="flex items-center gap-2 mt-2">
                          <Badge variant="secondary" className="capitalize">
                            {item.billingCycle === "monthly" ? "–ï–∂–µ–º–µ—Å—è—á–Ω–æ" : "–ï–∂–µ–≥–æ–¥–Ω–æ"}
                          </Badge>
                        </div>
                      </div>
                    </div>
                    <div className="text-right flex flex-col items-end gap-3">
                      <p className="text-2xl font-bold" data-testid={`text-price-${item.id}`}>
                        ${item.price.toFixed(2)}
                      </p>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => toggleExpanded(item.id)}
                          data-testid={`button-toggle-credentials-${item.id}`}
                        >
                          {isExpanded ? (
                            <ChevronUp className="h-4 w-4" />
                          ) : (
                            <ChevronDown className="h-4 w-4" />
                          )}
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleRemoveItem(item.id)}
                          data-testid={`button-remove-${item.id}`}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </div>

                  {isExpanded && (
                    <div className="mt-6 pt-6 border-t space-y-4">
                      <h4 className="font-semibold text-sm">–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –¥–æ—Å—Ç—É–ø–µ</h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor={`login-${item.id}`} className="text-xs">
                            –õ–æ–≥–∏–Ω –≤ —Å–µ—Ä–≤–∏—Å–µ
                          </Label>
                          <Input
                            id={`login-${item.id}`}
                            type="text"
                            placeholder="username –∏–ª–∏ email"
                            value={item.login || ""}
                            onChange={(e) => handleCredentialsUpdate(item.id, "login", e.target.value)}
                            data-testid={`input-login-${item.id}`}
                          />
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor={`password-${item.id}`} className="text-xs">
                            –ü–∞—Ä–æ–ª—å –æ—Ç —Å–µ—Ä–≤–∏—Å–∞
                          </Label>
                          <Input
                            id={`password-${item.id}`}
                            type="password"
                            placeholder="–ü–∞—Ä–æ–ª—å"
                            value={item.password || ""}
                            onChange={(e) => handleCredentialsUpdate(item.id, "password", e.target.value)}
                            data-testid={`input-password-${item.id}`}
                          />
                        </div>
                        <div className="space-y-2 md:col-span-2">
                          <Label htmlFor={`payment-url-${item.id}`} className="text-xs">
                            –°—Å—ã–ª–∫–∞ –Ω–∞ –æ–ø–ª–∞—Ç—É —Å–µ—Ä–≤–∏—Å–∞
                          </Label>
                          <Input
                            id={`payment-url-${item.id}`}
                            type="url"
                            placeholder="https://..."
                            value={item.paymentUrl || ""}
                            onChange={(e) => handleCredentialsUpdate(item.id, "paymentUrl", e.target.value)}
                            data-testid={`input-payment-url-${item.id}`}
                          />
                        </div>
                      </div>
                    </div>
                  )}
                </CardContent>
              </Card>
            );
          })}
        </div>

        <div className="lg:col-span-1">
          <Card className="sticky top-24">
            <CardHeader>
              <h3 className="text-xl font-semibold">–û—Ñ–æ—Ä–º–ª–µ–Ω–∏–µ –∑–∞–∫–∞–∑–∞</h3>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-muted-foreground">–¢–æ–≤–∞—Ä–æ–≤:</span>
                  <span className="font-semibold">{cartItems.length}</span>
                </div>
                <div className="flex justify-between pt-3 border-t">
                  <span className="font-semibold">–ò—Ç–æ–≥–æ:</span>
                  <span className="text-2xl font-bold" data-testid="text-total-price">
                    ${totalPrice.toFixed(2)}
                  </span>
                </div>
              </div>

              <div className="space-y-3">
