                              ) : (
                                <User className="h-4 w-4" />
                              )}
                              <span>
                                {payer.type === "company" ? payer.companyName : `${payer.firstName} ${payer.lastName}`}
                              </span>
                            </div>
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>

                    {selectedPayer && (
                      <div className="p-4 border border-border rounded-md space-y-2">
                        <div className="flex items-center justify-between">
                          <span className="text-sm text-muted-foreground">–ë–∞–ª–∞–Ω—Å:</span>
                          <span className="font-semibold">${selectedPayer.balance.toFixed(2)}</span>
                        </div>
                        <div className="flex items-center justify-between pt-2 border-t">
                          <span className="text-sm text-muted-foreground">–ü–æ—Å–ª–µ –æ–ø–ª–∞—Ç—ã:</span>
                          <span className={`font-semibold ${hasInsufficientFunds ? 'text-destructive' : 'text-green-500'}`}>
                            ${(selectedPayer.balance - totalPrice).toFixed(2)}
                          </span>
                        </div>

                        {hasInsufficientFunds && (
                          <div className="flex items-start gap-2 p-3 bg-destructive/10 border border-destructive/20 rounded-md mt-3">
                            <AlertTriangle className="h-4 w-4 text-destructive mt-0.5 flex-shrink-0" />
                            <div>
                              <p className="text-sm font-medium text-destructive">–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤</p>
                              <p className="text-xs text-destructive/80 mt-1">
                                –ù–µ —Ö–≤–∞—Ç–∞–µ—Ç ${(totalPrice - selectedPayer.balance).toFixed(2)}
                              </p>
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </>
                ) : (
                  <div className="p-4 bg-muted rounded-md text-center">
                    <p className="text-sm text-muted-foreground">
                      –ù–µ—Ç –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–æ–≤. <a href="/finances" className="text-primary underline">–°–æ–∑–¥–∞—Ç—å</a>
                    </p>
                  </div>
                )}
              </div>

              <Button 
                className="w-full gap-2" 
                size="lg"
                disabled={hasInsufficientFunds || !selectedPayer}
                onClick={handleCheckout}
                data-testid="button-checkout"
              >
                <ShoppingBag className="h-5 w-5" />
                –û–ø–ª–∞—Ç–∏—Ç—å ${totalPrice.toFixed(2)}
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

