import { useState, useEffect } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Check, ShoppingBag, CheckCircle2, CreditCard, Building2, User, AlertTriangle, Info, Clock, Smartphone, Wallet, FileText, Download } from "lucide-react";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { Payer, Transaction } from "@/lib/payersData";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface PricingTier {
  name: string;
  price: string;
  features: string[];
}

interface PurchaseDialogProps {
  service: {
    id: string;
    name: string;
    logoUrl?: string;
    color: string;
    pricingTiers: PricingTier[];
  };
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
  trigger?: React.ReactNode;
}

export default function PurchaseDialog({ service, open: externalOpen, onOpenChange: externalOnOpenChange, trigger }: PurchaseDialogProps) {
  const [internalOpen, setInternalOpen] = useState(false);
  const open = externalOpen !== undefined ? externalOpen : internalOpen;
  const setOpen = externalOnOpenChange || setInternalOpen;
  const [selectedPlan, setSelectedPlan] = useState<number>(1); // Default to middle tier
  const [billingCycle, setBillingCycle] = useState<"monthly" | "yearly">("monthly");
  const [step, setStep] = useState<"select" | "confirm" | "payment" | "success">("select");
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [payers, setPayers] = useState<Payer[]>([]);
  const [selectedPayerId, setSelectedPayerId] = useState<string>("");
  const [login, setLogin] = useState("");
  const [password, setPassword] = useState("");
  const [paymentUrl, setPaymentUrl] = useState("");
  const [topUpAmount, setTopUpAmount] = useState<string>("");
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState<string>("card");

  useEffect(() => {
    if (open) {
      const stored = localStorage.getItem("payers");
      if (stored) {
        const payersData = JSON.parse(stored);
        setPayers(payersData);
        if (payersData.length > 0 && !selectedPayerId) {
          setSelectedPayerId(payersData[0].id);
        }
      }
    }
  }, [open]);

  const selectedPayer = payers.find(p => p.id === selectedPayerId);
  const tier = service.pricingTiers[selectedPlan];
  const priceValue = parseFloat(tier.price.replace(/[^0-9.]/g, '')) || 0;
  const hasInsufficientFunds = selectedPayer ? selectedPayer.balance < priceValue : true;

  const handleConfirmPurchase = () => {
    if (!selectedPayer) {
      toast({
        title: "–í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞",
        variant: "destructive",
      });
      return;
    }

    // All payers go to payment screen
    setStep("payment");
  };

  const generateInvoice = () => {
    if (!selectedPayer || selectedPayer.type !== "company") return "";

    const invoiceData = `
–°–ß–ï–¢ –ù–ê –û–ü–õ–ê–¢–£ ‚Ññ${Date.now()}
–æ—Ç ${new Date().toLocaleDateString('ru-RU')}

–ü–ª–∞—Ç–µ–ª—å—â–∏–∫: ${selectedPayer.companyName}
–ò–ù–ù: ${selectedPayer.inn || ""}
–ö–ü–ü: ${selectedPayer.kpp || ""}

–£—Å–ª—É–≥–∞: ${service.name} - ${tier.name}
–ü–µ—Ä–∏–æ–¥ –æ–ø–ª–∞—Ç—ã: ${billingCycle === "monthly" ? "–ï–∂–µ–º–µ—Å—è—á–Ω–æ" : "–ï–∂–µ–≥–æ–¥–Ω–æ"}
–°—Ç–æ–∏–º–æ—Å—Ç—å: ${priceValue} ‚ÇΩ

–í—Å–µ–≥–æ –∫ –æ–ø–ª–∞—Ç–µ: ${priceValue} ‚ÇΩ
    `.trim();

    const blob = new Blob([invoiceData], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    return url;
  };

  const completePurchase = () => {
    if (!selectedPayer) return;

    let invoiceUrl = undefined;
    let purchaseStatus: "active" | "pending_payment" = "active";

    if (selectedPaymentMethod === "invoice") {
      invoiceUrl = generateInvoice();
      purchaseStatus = "pending_payment";
    }

    const purchase = {
      id: Date.now().toString(),
      serviceId: service.id,
      planName: tier.name,
      price: priceValue,
      purchaseDate: new Date().toISOString(),
      status: purchaseStatus,
      billingCycle: billingCycle,
      payerId: selectedPayerId,
      paymentMethod: selectedPaymentMethod,
      invoiceUrl,
      login: login || undefined,
      password: password || undefined,
      paymentUrl: paymentUrl || undefined,
    };

    const existing = localStorage.getItem("userPurchases");
    const purchases = existing ? JSON.parse(existing) : [];
    purchases.push(purchase);
    localStorage.setItem("userPurchases", JSON.stringify(purchases));

    // Deduct from payer balance
    const updatedPayers = payers.map(p => 
      p.id === selectedPayerId ? { ...p, balance: p.balance - priceValue } : p
    );
    setPayers(updatedPayers);
    localStorage.setItem("payers", JSON.stringify(updatedPayers));

    // Add transaction
    const newTransaction: Transaction = {
      id: Date.now().toString(),
      payerId: selectedPayerId,
      date: new Date().toISOString(),
      type: "purchase",
      amount: priceValue,
      comment: `–ü–æ–∫—É–ø–∫–∞ ${service.name} - ${tier.name}`,
      serviceId: service.id,
      serviceName: service.name,
    };
    const existingTransactions = localStorage.getItem("transactions");
    const transactions = existingTransactions ? JSON.parse(existingTransactions) : [];
    transactions.push(newTransaction);
    localStorage.setItem("transactions", JSON.stringify(transactions));

    setStep("success");

    toast({
      title: "Purchase successful!",
      description: `You've subscribed to ${service.name} - ${tier.name}`,
    });
  };

  const handleClose = () => {
    setOpen(false);
    setTimeout(() => {
      setStep("select");
      setSelectedPlan(1);
      setBillingCycle("monthly");
      setLogin("");
      setPassword("");
      setPaymentUrl("");
    }, 200);
  };

  const handleViewAccount = () => {
    handleClose();
    setLocation("/account");
  };

  const selectedTier = service.pricingTiers[selectedPlan];

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      {trigger ? (
        <DialogTrigger asChild>
          {trigger}
        </DialogTrigger>
      ) : externalOpen === undefined ? (
        <DialogTrigger asChild>
          <Button 
            size="lg" 
            className="gap-2"
            data-testid="button-purchase-service"
          >
            <ShoppingBag className="h-5 w-5" />
            Subscribe Now
          </Button>
        </DialogTrigger>
      ) : null}
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        {step === "select" && (
          <>
            <DialogHeader>
              <div className="flex items-center gap-4 mb-2">
                {service.logoUrl ? (
                  <img 
                    src={service.logoUrl} 
                    alt={service.name}
                    className="w-12 h-12 object-contain rounded-md"
                  />
                ) : (
                  <div 
                    className="w-12 h-12 rounded-md flex items-center justify-center"
                    style={{ backgroundColor: service.color }}
                  >
                    <span className="text-white font-bold text-xl">
                      {service.name.charAt(0)}
                    </span>
                  </div>
                )}
                <DialogTitle className="text-2xl">Subscribe to {service.name}</DialogTitle>
              </div>
              <DialogDescription>
                Choose your plan and billing cycle to get started
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-6 mt-6">
              <div className="flex items-center justify-center gap-2 p-1 bg-muted rounded-md w-fit mx-auto">
                <Button
                  variant={billingCycle === "monthly" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setBillingCycle("monthly")}
                  data-testid="button-billing-monthly"
                >
                  Monthly
                </Button>
                <Button
                  variant={billingCycle === "yearly" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setBillingCycle("yearly")}
                  data-testid="button-billing-yearly"
                >
                  Yearly
                  <Badge variant="secondary" className="ml-2">Save 20%</Badge>
                </Button>
              </div>

              <RadioGroup value={selectedPlan.toString()} onValueChange={(v) => setSelectedPlan(parseInt(v))}>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  {service.pricingTiers.map((tier, index) => (
                    <Card 
                      key={index} 
                      className={`cursor-pointer transition-all ${
                        selectedPlan === index ? 'border-primary ring-2 ring-primary/20' : 'hover-elevate'
                      }`}
                      onClick={() => setSelectedPlan(index)}
                      data-testid={`card-plan-${index}`}
                    >
                      <CardHeader className="pb-4">
                        <div className="flex items-center justify-between mb-2">
                          <h3 className="font-semibold text-lg">{tier.name}</h3>
                          <RadioGroupItem value={index.toString()} id={`plan-${index}`} />
                        </div>
                        <div className="flex items-baseline gap-1">
                          <span className="text-3xl font-bold">{tier.price}</span>
                          <span className="text-muted-foreground text-sm">
                            /{billingCycle === "monthly" ? "mo" : "yr"}
                          </span>
                        </div>
                      </CardHeader>
                      <CardContent className="space-y-2">
                        {tier.features.slice(0, 4).map((feature, idx) => (
                          <div key={idx} className="flex items-start gap-2">
                            <Check className="h-4 w-4 text-primary mt-0.5 flex-shrink-0" />
                            <span className="text-sm text-muted-foreground">{feature}</span>
                          </div>
                        ))}
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </RadioGroup>

              <div className="flex justify-end gap-3 pt-4 border-t">
                <Button variant="outline" onClick={handleClose} data-testid="button-cancel-purchase">
                  Cancel
                </Button>
                <Button onClick={() => setStep("confirm")} data-testid="button-continue-purchase">
                  Continue
                </Button>
              </div>
            </div>
          </>
        )}

        {step === "confirm" && (
          <>
            <DialogHeader>
              <DialogTitle className="text-2xl">Confirm Your Purchase</DialogTitle>
              <DialogDescription>
                Review your subscription details before confirming
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-6 mt-6">
              <Card>
                <CardContent className="pt-6 space-y-4">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center gap-4">
                      {service.logoUrl ? (
                        <img 
                          src={service.logoUrl} 
                          alt={service.name}
                          className="w-16 h-16 object-contain rounded-md"
                        />
                      ) : (
                        <div 
                          className="w-16 h-16 rounded-md flex items-center justify-center"
                          style={{ backgroundColor: service.color }}
                        >
                          <span className="text-white font-bold text-2xl">
                            {service.name.charAt(0)}
                          </span>
                        </div>
                      )}
                      <div>
                        <h3 className="font-semibold text-lg">{service.name}</h3>
                        <p className="text-sm text-muted-foreground">{selectedTier.name} Plan</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-2xl font-bold">{selectedTier.price}</p>
                      <p className="text-sm text-muted-foreground capitalize">
                        Billed {billingCycle}
                      </p>
                    </div>
                  </div>

                  <div className="pt-4 border-t">
                    <h4 className="font-semibold mb-3">Included Features:</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                      {selectedTier.features.map((feature, idx) => (
                        <div key={idx} className="flex items-start gap-2">
                          <Check className="h-4 w-4 text-primary mt-0.5 flex-shrink-0" />
                          <span className="text-sm text-muted-foreground">{feature}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <Info className="h-5 w-5" />
                    <h3 className="font-semibold">–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –¥–æ—Å—Ç—É–ø–µ</h3>
                  </div>
                  <p className="text-sm text-muted-foreground mt-2">
                    –õ–æ–≥–∏–Ω –∏ –ø–∞—Ä–æ–ª—å –≤–≤–æ–¥—è—Ç—Å—è –≤—Ä—É—á–Ω—É—é. –†–µ–∫–æ–º–µ–Ω–¥—É–µ–º –∑–∞–ø—Ä–æ—Å–∏—Ç—å —ç—Ç–æ —É –¥–∞–Ω–Ω–æ–≥–æ —Å–µ—Ä–≤–∏—Å–∞, –∏–ª–∏ —Å–æ–∑–¥–∞—Ç—å –æ—Ç–¥–µ–ª—å–Ω—ã–π –∞–∫–∫–∞—É–Ω—Ç –¥–ª—è –Ω–µ–≥–æ.
                  </p>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="service-login">–õ–æ–≥–∏–Ω –≤ —Å–µ—Ä–≤–∏—Å–µ</Label>
                    <Input
                      id="service-login"
                      type="text"
                      placeholder="username –∏–ª–∏ email"
                      value={login}
                      onChange={(e) => setLogin(e.target.value)}
                      data-testid="input-service-login"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="service-password">–ü–∞—Ä–æ–ª—å –æ—Ç —Å–µ—Ä–≤–∏—Å–∞</Label>
                    <Input
                      id="service-password"
                      type="password"
                      placeholder="–ü–∞—Ä–æ–ª—å"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      data-testid="input-service-password"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="payment-url">–°—Å—ã–ª–∫–∞ –Ω–∞ –æ–ø–ª–∞—Ç—É —Å–µ—Ä–≤–∏—Å–∞</Label>
                    <Input
                      id="payment-url"
                      type="url"
                      placeholder="https://..."
                      value={paymentUrl}
                      onChange={(e) => setPaymentUrl(e.target.value)}
                      data-testid="input-payment-url"
                    />
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <CreditCard className="h-5 w-5" />
                    <h3 className="font-semibold">–ü–ª–∞—Ç–µ–ª—å—â–∏–∫</h3>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  {payers.length > 0 ? (
                    <>
                      <Select value={selectedPayerId} onValueChange={setSelectedPayerId}>
                        <SelectTrigger data-testid="select-payer">
                          <SelectValue placeholder="–í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞" />
                        </SelectTrigger>
                        <SelectContent>
                          {payers.map((payer) => (
                            <SelectItem key={payer.id} value={payer.id}>
                              <div className="flex items-center gap-2">
                                {payer.type === "company" ? (
                                  <Building2 className="h-4 w-4" />
                                ) : (
                                  <User className="h-4 w-4" />
                                )}
                                <span>
                                  {payer.type === "company" ? payer.companyName : `${payer.lastName} ${payer.firstName}`}
                                </span>
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>

                      {selectedPayer && (
                        <div className="p-4 border border-border rounded-md space-y-2">
                          <div className="flex items-center justify-between">
                            <span className="text-sm text-muted-foreground">–¢–µ–∫—É—â–∏–π –±–∞–ª–∞–Ω—Å:</span>
                            <span className="font-semibold">${selectedPayer.balance.toFixed(2)}</span>
                          </div>
                          <div className="flex items-center justify-between">
                            <span className="text-sm text-muted-foreground">–°—Ç–æ–∏–º–æ—Å—Ç—å:</span>
                            <span className="font-semibold">${priceValue.toFixed(2)}</span>
                          </div>
                          <div className="flex items-center justify-between pt-2 border-t">
                            <span className="text-sm text-muted-foreground">–û—Å—Ç–∞—Ç–æ–∫ –ø–æ—Å–ª–µ –ø–æ–∫—É–ø–∫–∏:</span>
                            <span className={`font-semibold ${hasInsufficientFunds ? 'text-destructive' : ''}`}>
                              ${(selectedPayer.balance - priceValue).toFixed(2)}
                            </span>
                          </div>

                          {hasInsufficientFunds && (
                            <div className="flex items-start gap-2 p-3 bg-destructive/10 border border-destructive/20 rounded-md mt-3">
                              <AlertTriangle className="h-4 w-4 text-destructive mt-0.5 flex-shrink-0" />
                              <div>
                                <p className="text-sm font-medium text-destructive">–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤</p>
                                <p className="text-xs text-destructive/80 mt-1">
                                  –ü–æ–ø–æ–ª–Ω–∏—Ç–µ –±–∞–ª–∞–Ω—Å –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞ –Ω–∞ ${(priceValue - selectedPayer.balance).toFixed(2)} –∏–ª–∏ –≤—ã–±–µ—Ä–∏—Ç–µ –¥—Ä—É–≥–æ–≥–æ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞
                                </p>
                              </div>
                            </div>
                          )}
                        </div>
                      )}
                    </>
                  ) : (
                    <div className="p-4 bg-muted rounded-md text-center">
                      <p className="text-sm text-muted-foreground">
                        –£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–æ–≤. –°–æ–∑–¥–∞–π—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞ –≤ —Ä–∞–∑–¥–µ–ª–µ <a href="/finances" className="text-primary underline">–§–∏–Ω–∞–Ω—Å—ã</a>
                      </p>
                    </div>
                  )}
                </CardContent>
              </Card>

              <div className="flex justify-end gap-3 pt-4 border-t">
                <Button variant="outline" onClick={() => setStep("select")} data-testid="button-back-purchase">
                  Back
                </Button>
                <Button 
                  onClick={handleConfirmPurchase} 
                  disabled={!selectedPayer}
                  data-testid="button-confirm-purchase"
                >
                  Confirm Purchase
                </Button>
              </div>
            </div>
          </>
        )}

        {step === "payment" && selectedPayer && (
          <>
            <DialogHeader>
              <DialogTitle className="text-2xl">–ü–æ–ø–æ–ª–Ω–∏—Ç—å —Å—á–µ—Ç</DialogTitle>
              <DialogDescription>
                –í—ã–±–µ—Ä–∏—Ç–µ —Å–ø–æ—Å–æ–± –ø–æ–ø–æ–ª–Ω–µ–Ω–∏—è –±–∞–ª–∞–Ω—Å–∞ –¥–ª—è –æ–ø–ª–∞—Ç—ã
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-6 mt-6">
              {/* Payer Info */}
              <Card className="bg-blue-500/5 border-blue-500/20">
                <CardContent className="pt-6">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-blue-500/10 rounded-full flex items-center justify-center">
                      <User className="h-5 w-5 text-blue-500" />
                    </div>
                    <div>
                      <h3 className="font-semibold">
                        {selectedPayer.type === "individual" 
                          ? `${selectedPayer.lastName} ${selectedPayer.firstName}`
                          : selectedPayer.companyName
                        }
                      </h3>
                      <p className="text-sm text-muted-foreground">
                        {selectedPayer.type === "individual" ? "–§–∏–∑–∏—á–µ—Å–∫–æ–µ –ª–∏—Ü–æ" : "–Æ—Ä–∏–¥–∏—á–µ—Å–∫–æ–µ –ª–∏—Ü–æ"}
                      </p>
                    </div>
                  </div>
                  <div className="mt-4 pt-4 border-t">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">–¢–µ–∫—É—â–∏–π –±–∞–ª–∞–Ω—Å:</span>
                      <span className="font-semibold">{selectedPayer.balance.toFixed(2)} ‚ÇΩ</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Top-up Amount - Only for Individual */}
              {selectedPayer.type === "individual" && (
                <Card>
