import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { CreditCard, Building2, User } from "lucide-react";
import { type Payer, PAYMENT_METHODS } from "@/lib/payersData";
import { useToast } from "@/hooks/use-toast";
import { Card } from "@/components/ui/card";

interface WithdrawDialogProps {
  payers: Payer[];
  onWithdraw: (payerId: string, amount: number, method: string) => void;
}

export default function WithdrawDialog({ payers, onWithdraw }: WithdrawDialogProps) {
  const [open, setOpen] = useState(false);
  const [selectedPayer, setSelectedPayer] = useState("");
  const [amount, setAmount] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const { toast } = useToast();

  const quickAmounts = [500, 1000, 2000, 5000, 10000];

  const handleWithdraw = () => {
    const amountNum = parseFloat(amount);

    if (!selectedPayer) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞",
        variant: "destructive",
      });
      return;
    }

    if (!amountNum || amountNum <= 0) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—É—é —Å—É–º–º—É",
        variant: "destructive",
      });
      return;
    }

    const payer = payers.find(p => p.id === selectedPayer);
    if (payer && payer.balance < amountNum) {
      toast({
        title: "–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤",
        description: `–ù–∞ –±–∞–ª–∞–Ω—Å–µ ${payer.balance} ‚ÇΩ, –∞ —Ç—Ä–µ–±—É–µ—Ç—Å—è ${amountNum} ‚ÇΩ`,
        variant: "destructive",
      });
      return;
    }

    const method = PAYMENT_METHODS.find(m => m.id === paymentMethod);
    onWithdraw(selectedPayer, amountNum, method?.name || paymentMethod);

    toast({
      title: "–°—Ä–µ–¥—Å—Ç–≤–∞ —Å–ø–∏—Å–∞–Ω—ã",
      description: `–£—Å–ø–µ—à–Ω–æ —Å–ø–∏—Å–∞–Ω–æ ${amountNum} ‚ÇΩ`,
    });

    setOpen(false);
    setAmount("");
    setSelectedPayer("");
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button variant="default" className="gap-2 bg-orange-600 hover:bg-orange-700" data-testid="button-withdraw">
          <CreditCard className="h-4 w-4" />
          –°–ø–∏—Å–∞—Ç—å —Å–æ —Å—á–µ—Ç–∞
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-lg">
        <DialogHeader>
          <DialogTitle>–°–ø–∏—Å–∞—Ç—å —Å–æ —Å—á–µ—Ç–∞</DialogTitle>
          <DialogDescription>
            –í—ã–±–µ—Ä–∏—Ç–µ –ø–ª–∞—Ç–µ–ª—å—â–∏–∫–∞ –∏ —Å—É–º–º—É –¥–ª—è —Å–ø–∏—Å–∞–Ω–∏—è
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          <div className="space-y-3">
            <Label>–ü–ª–∞—Ç–µ–ª—å—â–∏–∫–∏</Label>
            <div className="space-y-2">
              {payers.map((payer) => (
                <Card
                  key={payer.id}
                  className={`p-4 cursor-pointer hover-elevate transition-all ${
                    selectedPayer === payer.id ? "ring-2 ring-primary" : ""
                  }`}
                  onClick={() => setSelectedPayer(payer.id)}
                  data-testid={`select-withdraw-payer-${payer.id}`}
                >
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-md bg-primary/10 flex items-center justify-center">
                      {payer.type === "company" ? (
                        <Building2 className="h-5 w-5 text-primary" />
                      ) : (
                        <User className="h-5 w-5 text-primary" />
                      )}
                    </div>
                    <div className="flex-1">
                      <p className="font-medium">{payer.name}</p>
                      <p className="text-sm text-muted-foreground">
                        –¢–∏–ø: {payer.type === "company" ? "company" : "individual"}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-muted-foreground">–ë–∞–ª–∞–Ω—Å:</p>
                      <p className="font-semibold">
                        {payer.balance.toLocaleString('ru-RU')} ‚ÇΩ
                      </p>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="withdraw-amount">–°–ø–∏—Å–∞—Ç—å —Å–æ —Å—á–µ—Ç–∞ *</Label>
            <Input
              id="withdraw-amount"
              type="number"
              placeholder="2000"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              data-testid="input-withdraw-amount"
            />
            <div className="flex gap-2 flex-wrap mt-2">
              {quickAmounts.map((qa) => (
                <Button
                  key={qa}
                  variant="outline"
                  size="sm"
                  onClick={() => setAmount(qa.toString())}
                  data-testid={`button-withdraw-quick-${qa}`}
                >
                  {qa} ‚ÇΩ
                </Button>
              ))}
            </div>
          </div>

          <div className="space-y-2">
            <Label>–í—ã–±–µ—Ä–∏—Ç–µ —Å–ø–æ—Å–æ–± –æ–ø–ª–∞—Ç—ã</Label>
            <RadioGroup value={paymentMethod} onValueChange={setPaymentMethod}>
              {PAYMENT_METHODS.map((method) => (
                <div 
                  key={method.id}
                  className="flex items-center space-x-2 p-3 border border-border rounded-md hover-elevate cursor-pointer"
                  onClick={() => setPaymentMethod(method.id)}
                >
                  <RadioGroupItem value={method.id} id={`withdraw-${method.id}`} />
                  <Label htmlFor={`withdraw-${method.id}`} className="flex items-center gap-3 cursor-pointer flex-1">
                    <span className="text-2xl">{method.icon}</span>
                    <div>
                      <p className="font-medium">{method.name}</p>
                      <p className="text-sm text-muted-foreground">{method.description}</p>
                    </div>
                  </Label>
                </div>
              ))}
            </RadioGroup>
          </div>
        </div>

        <div className="flex justify-end gap-2">
          <Button variant="outline" onClick={() => setOpen(false)}>
            –û—Ç–º–µ–Ω–∞
          </Button>
          <Button onClick={handleWithdraw} className="bg-orange-600 hover:bg-orange-700" data-testid="button-confirm-withdraw">
            –°–ø–∏—Å–∞—Ç—å
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}
